import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-user-manage',
  templateUrl: './user-manage.component.html',
  styleUrls: ['./user-manage.component.scss']
})
export class UserManageComponent implements OnInit {
  userId: number | null = null;
  user: any = {
    name: '',
    email: '',
    role: ''
  };
  isEditMode: boolean = false;

  constructor(
    private http: HttpClient,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    // Check if there is an ID in the URL for edit mode
    this.route.params.subscribe(params => {
      if (params['id']) {
        this.userId = params['id'];
        this.isEditMode = true;
        this.getUserDetails(this.userId!);
      }
    });
  }

  // Fetch the details of a specific user for editing
  getUserDetails(id: number) {
    this.http.get<any>(`http://localhost:7164/api/User/${id}`).subscribe(
      (data) => {
        this.user = data;
      },
      (error) => {
        console.error('Error fetching user data', error);
      }
    );
  }

  // Save the user (either create or update)
  saveUser() {
    if (this.isEditMode) {
      this.updateUser();
    } else {
      this.createUser();
    }
  }

  // Create a new user
  createUser() {
    this.http.post('http://localhost:7164/api/User', this.user).subscribe(
      (response) => {
        console.log('User created successfully', response);
        this.router.navigate(['/users']);
      },
      (error) => {
        console.error('Error creating user', error);
      }
    );
  }

  // Update existing user
  updateUser() {
    this.http.put(`http://localhost:7164/api/User/${this.userId}`, this.user).subscribe(
      (response) => {
        console.log('User updated successfully', response);
        this.router.navigate(['/users']);
      },
      (error) => {
        console.error('Error updating user', error);
      }
    );
  }

  // Cancel edit mode
  cancelEdit() {
    this.router.navigate(['/users']);
  }
}
