import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../user-list/user.service';

@Component({
  selector: 'app-user-manage',
  templateUrl: './user-manage.component.html',
  styleUrls: ['./user-manage.component.scss']
})
export class UserManageComponent implements OnInit {
  user: any = { id: 0, name: '', email: '' }; // Varsayılan boş kullanıcı
  isEditing: boolean = false;
  isLoading: boolean = true;

  constructor(
    private userService: UserService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    const userId = this.route.snapshot.paramMap.get('id');
    if (userId) {
      this.isEditing = true;
      this.userService.getUserById(userId).subscribe({
        next: (data: any) => {
          this.user = data; 
          console.log("Kullanıcı:", data);

          this.isLoading = false;
        },
        error: () => {
          this.isLoading = false;
        }
      });
    } else {
      this.isEditing = false;
      this.isLoading = false;
    }
  }

  saveUser(): void {
    if (this.isEditing) {
      this.userService.updateUser(this.user).subscribe({
        next: () => {
          console.log("Güncellenen Kullanıcı:", this.user);

          this.router.navigate(['/admin/user/list']);  // Kullanıcı listeleme sayfasına yönlendirme
        },
        error: () => {
          // Hata yönetimi
        }
      });
    } else {
      this.userService.createUser(this.user).subscribe({
        next: () => {
          this.router.navigate(['/admin/user/list']);  // Yeni kullanıcı eklendikten sonra yönlendirme
        },
        error: () => {
          // Hata yönetimi
        }
      });
    }
  }

  cancel(): void {
    this.router.navigate(['/admin/user/list']);  // İptal durumunda geri yönlendirme
  }
}
