// layout.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.scss']
})
export class LayoutComponent {
  isSidebarActive: boolean = false;

  toggleSidebar(): void {
    this.isSidebarActive = !this.isSidebarActive;
  }
}
